INSERT INTO products(id, name, price, url, snippet, description, accreditation, category) VALUES(2, 'Headphones', 249.99, 'https://images.unsplash.com/photo-1583394838336-acd977736f90?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80', 'Listen to stuff!', 'Headphones are a pair of small loudspeaker drivers worn on or around the head over a users ears. They are electroacoustic transducers, which convert an electrical signal to a corresponding sound. Headphones let a single user listen to an audio source privately, in contrast to a loudspeaker, which emits sound into the open air for anyone nearby to hear.', 'wikipedia.org', 'stationary');
INSERT INTO products(id, name, price, url, snippet, description, accreditation, category) VALUES(3, 'Backpack', 79.99, 'https://images.unsplash.com/photo-1553062407-98eeb64c6a62?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80', 'Carry things around town!', 'A backpack—also called  knapsack, rucksack, rucksac, pack, sackpack, booksack, bookbag or backsack—is, in its simplest frameless form, a fabric sack carried on ones back and secured with two straps that go over the shoulders, but it can have an external frame, internal frame, and there are bodypacks.\nBackpacks are commonly used by hikers and students, and are often preferred to handbags for carrying heavy loads or carrying any sort of equipment, because of the limited capacity to carry heavy weights for long periods of time in the hands.\nLarge backpacks, used to carry loads over 10 kilograms (22 lb), as well as smaller sports backpacks ', 'wikipedia.org', 'stationary');
